import React, { useState } from 'react';
import {
  View,
  Text,
  TextInput,
  TouchableOpacity,
  SafeAreaView,
  ScrollView,
  StatusBar,
  CheckBox,
} from 'react-native';
import { Ionicons } from '@expo/vector-icons';
import { globalStyles } from '../styles/globalStyles';

interface LeaveScreenProps {
  onBack: () => void;
  onApplyLeave: () => void;
}

const LeaveScreen: React.FC<LeaveScreenProps> = ({ onBack, onApplyLeave }) => {
  const [formData, setFormData] = useState({
    halfDay: false,
    leaveType: '',
    fromDate: '',
    toDate: '',
    email: '',
    reason: '',
  });

  const handleInputChange = (field: string, value: string | boolean): void => {
    setFormData(prev => ({
      ...prev,
      [field]: value
    }));
  };

  const handleApplyLeave = (): void => {
    console.log('Applying leave:', formData);
    onApplyLeave();
  };

  return (
    <SafeAreaView style={globalStyles.complaintContainer}>
      <StatusBar barStyle="light-content" backgroundColor="#3498db" />
      
      {/* Header */}
      <View style={globalStyles.complaintHeader}>
        <TouchableOpacity onPress={onBack} style={globalStyles.complaintBackButton}>
          <Ionicons name="arrow-back" size={24} color="#fff" />
        </TouchableOpacity>
        
        <Text style={{ color: '#fff', fontSize: 18, fontWeight: 'bold' }}>Leave</Text>
        
        <View style={{ width: 40 }} />
      </View>

      {/* Form Content */}
      <ScrollView style={globalStyles.complaintContent} showsVerticalScrollIndicator={false}>
        {/* Half Day Checkbox */}
        <View style={[globalStyles.complaintFieldContainer, { flexDirection: 'row', alignItems: 'center' }]}>
          <CheckBox
            value={formData.halfDay}
            onValueChange={(value) => handleInputChange('halfDay', value)}
            style={{ marginRight: 12 }}
          />
          <Text style={[globalStyles.complaintFieldLabel, { marginBottom: 0 }]}>Half Day</Text>
        </View>

        {/* Leave Type */}
        <View style={globalStyles.complaintFieldContainer}>
          <Text style={globalStyles.complaintFieldLabel}>Leave Type</Text>
          <View style={[globalStyles.complaintDropdownContainer, { backgroundColor: '#f5f5f5' }]}>
            <TextInput
              style={[globalStyles.complaintDropdownInput, { backgroundColor: 'transparent' }]}
              placeholder="Select Leave Type"
              value={formData.leaveType}
              onChangeText={(value) => handleInputChange('leaveType', value)}
              editable={false}
            />
            <Ionicons name="chevron-down" size={20} color="#666" />
          </View>
        </View>

        {/* From Date */}
        <View style={globalStyles.complaintFieldContainer}>
          <Text style={globalStyles.complaintFieldLabel}>From Date</Text>
          <View style={[globalStyles.complaintDropdownContainer, { backgroundColor: '#fff' }]}>
            <TextInput
              style={globalStyles.complaintDropdownInput}
              placeholder="DD/MM/YYYY"
              value={formData.fromDate}
              onChangeText={(value) => handleInputChange('fromDate', value)}
            />
            <Ionicons name="calendar-outline" size={20} color="#666" />
          </View>
        </View>

        {/* To Date */}
        <View style={globalStyles.complaintFieldContainer}>
          <Text style={globalStyles.complaintFieldLabel}>To Date</Text>
          <View style={[globalStyles.complaintDropdownContainer, { backgroundColor: '#fff' }]}>
            <TextInput
              style={globalStyles.complaintDropdownInput}
              placeholder="DD/MM/YYYY"
              value={formData.toDate}
              onChangeText={(value) => handleInputChange('toDate', value)}
            />
            <Ionicons name="calendar-outline" size={20} color="#666" />
          </View>
        </View>

        {/* Email */}
        <View style={globalStyles.complaintFieldContainer}>
          <Text style={globalStyles.complaintFieldLabel}>Email</Text>
          <View style={[globalStyles.complaintDropdownContainer, { backgroundColor: '#fff' }]}>
            <TextInput
              style={globalStyles.complaintDropdownInput}
              placeholder="Enter email address"
              value={formData.email}
              onChangeText={(value) => handleInputChange('email', value)}
              keyboardType="email-address"
              autoCapitalize="none"
            />
            <Ionicons name="mail-outline" size={20} color="#666" />
          </View>
        </View>

        {/* Reason */}
        <View style={globalStyles.complaintFieldContainer}>
          <Text style={globalStyles.complaintFieldLabel}>Reason</Text>
          <View style={[globalStyles.complaintMessageInput, { backgroundColor: '#fff', borderWidth: 1, borderColor: '#000' }]}>
            <TextInput
              style={[globalStyles.complaintMessageInput, { backgroundColor: 'transparent', borderWidth: 0 }]}
              placeholder="Enter reason for leave..."
              value={formData.reason}
              onChangeText={(value) => handleInputChange('reason', value)}
              multiline
              numberOfLines={4}
              textAlignVertical="top"
            />
          </View>
        </View>

        {/* Apply Leave Button */}
        <View style={[globalStyles.complaintFieldContainer, { marginTop: 30 }]}>
          <TouchableOpacity 
            style={[globalStyles.complaintSaveButton, { 
              backgroundColor: '#3498db', 
              borderRadius: 8, 
              paddingVertical: 16,
              alignItems: 'center',
              justifyContent: 'center'
            }]} 
            onPress={handleApplyLeave}
          >
            <Text style={[globalStyles.complaintSaveText, { marginLeft: 0 }]}>Apply Leave</Text>
          </TouchableOpacity>
        </View>
      </ScrollView>
    </SafeAreaView>
  );
};

export default LeaveScreen;
