import React, { useState } from 'react';
import {
  View,
  Text,
  TextInput,
  TouchableOpacity,
  SafeAreaView,
  ScrollView,
  StatusBar,
} from 'react-native';
import { Ionicons } from '@expo/vector-icons';
import { globalStyles } from '../styles/globalStyles';

interface MaterialRequisitionScreenProps {
  onBack: () => void;
  onSave: () => void;
}

const MaterialRequisitionScreen: React.FC<MaterialRequisitionScreenProps> = ({ onBack, onSave }) => {
  const [formData, setFormData] = useState({
    requisitionType: '',
    department: '',
    requestedBy: '',
    requestedDate: '',
    priority: '',
    items: [
      {
        id: 1,
        itemName: '',
        quantity: '',
        unit: '',
        description: '',
      }
    ],
    reason: '',
    approvalStatus: '',
  });

  const handleInputChange = (field: string, value: string): void => {
    setFormData(prev => ({
      ...prev,
      [field]: value
    }));
  };

  const handleItemChange = (itemId: number, field: string, value: string): void => {
    setFormData(prev => ({
      ...prev,
      items: prev.items.map(item => 
        item.id === itemId ? { ...item, [field]: value } : item
      )
    }));
  };

  const addNewItem = (): void => {
    const newItem = {
      id: formData.items.length + 1,
      itemName: '',
      quantity: '',
      unit: '',
      description: '',
    };
    setFormData(prev => ({
      ...prev,
      items: [...prev.items, newItem]
    }));
  };

  const removeItem = (itemId: number): void => {
    if (formData.items.length > 1) {
      setFormData(prev => ({
        ...prev,
        items: prev.items.filter(item => item.id !== itemId)
      }));
    }
  };

  const handleSave = (): void => {
    console.log('Saving material requisition:', formData);
    onSave();
  };

  return (
    <SafeAreaView style={globalStyles.complaintContainer}>
      <StatusBar barStyle="light-content" backgroundColor="#3498db" />
      
      {/* Header */}
      <View style={globalStyles.complaintHeader}>
        <TouchableOpacity onPress={onBack} style={globalStyles.complaintBackButton}>
          <Ionicons name="arrow-back" size={24} color="#fff" />
        </TouchableOpacity>
        
        <TouchableOpacity onPress={handleSave} style={globalStyles.complaintSaveButton}>
          <Ionicons name="checkmark" size={20} color="#fff" />
          <Text style={globalStyles.complaintSaveText}>Save</Text>
        </TouchableOpacity>
      </View>

      {/* Form Content */}
      <ScrollView style={globalStyles.complaintContent} showsVerticalScrollIndicator={false}>
        {/* Requisition Type */}
        <View style={globalStyles.complaintFieldContainer}>
          <Text style={globalStyles.complaintFieldLabel}>Requisition Type:</Text>
          <View style={globalStyles.complaintDropdownContainer}>
            <TextInput
              style={globalStyles.complaintDropdownInput}
              placeholder="Select Type"
              value={formData.requisitionType}
              onChangeText={(value) => handleInputChange('requisitionType', value)}
            />
            <Ionicons name="chevron-down" size={20} color="#666" />
          </View>
        </View>

        {/* Department */}
        <View style={globalStyles.complaintFieldContainer}>
          <Text style={globalStyles.complaintFieldLabel}>Department:</Text>
          <View style={globalStyles.complaintDropdownContainer}>
            <TextInput
              style={globalStyles.complaintDropdownInput}
              placeholder="Select Department"
              value={formData.department}
              onChangeText={(value) => handleInputChange('department', value)}
            />
            <Ionicons name="chevron-down" size={20} color="#666" />
          </View>
        </View>

        {/* Requested By */}
        <View style={globalStyles.complaintFieldContainer}>
          <Text style={globalStyles.complaintFieldLabel}>Requested By</Text>
          <TextInput
            style={globalStyles.complaintTextInput}
            placeholder="Enter Name"
            value={formData.requestedBy}
            onChangeText={(value) => handleInputChange('requestedBy', value)}
          />
        </View>

        {/* Requested Date */}
        <View style={globalStyles.complaintFieldContainer}>
          <Text style={globalStyles.complaintFieldLabel}>Requested Date</Text>
          <TextInput
            style={globalStyles.complaintTextInput}
            placeholder="DD/MM/YYYY"
            value={formData.requestedDate}
            onChangeText={(value) => handleInputChange('requestedDate', value)}
          />
        </View>

        {/* Priority */}
        <View style={globalStyles.complaintFieldContainer}>
          <Text style={globalStyles.complaintFieldLabel}>Priority:</Text>
          <View style={globalStyles.complaintDropdownContainer}>
            <TextInput
              style={globalStyles.complaintDropdownInput}
              placeholder="Select Priority"
              value={formData.priority}
              onChangeText={(value) => handleInputChange('priority', value)}
            />
            <Ionicons name="chevron-down" size={20} color="#666" />
          </View>
        </View>

        {/* Items Section */}
        <View style={globalStyles.complaintFieldContainer}>
          <View style={{ flexDirection: 'row', justifyContent: 'space-between', alignItems: 'center', marginBottom: 10 }}>
            <Text style={globalStyles.complaintFieldLabel}>Items Required</Text>
            <TouchableOpacity onPress={addNewItem} style={{ backgroundColor: '#3498db', paddingHorizontal: 12, paddingVertical: 6, borderRadius: 6 }}>
              <Text style={{ color: '#fff', fontSize: 14, fontWeight: '600' }}>+ Add Item</Text>
            </TouchableOpacity>
          </View>
          
          {formData.items.map((item, index) => (
            <View key={item.id} style={{ backgroundColor: '#f8f9fa', padding: 15, borderRadius: 8, marginBottom: 10, borderWidth: 1, borderColor: '#e1e8ed' }}>
              <View style={{ flexDirection: 'row', justifyContent: 'space-between', alignItems: 'center', marginBottom: 10 }}>
                <Text style={{ fontSize: 16, fontWeight: '600', color: '#2c3e50' }}>Item {index + 1}</Text>
                {formData.items.length > 1 && (
                  <TouchableOpacity onPress={() => removeItem(item.id)} style={{ backgroundColor: '#e74c3c', paddingHorizontal: 8, paddingVertical: 4, borderRadius: 4 }}>
                    <Text style={{ color: '#fff', fontSize: 12 }}>Remove</Text>
                  </TouchableOpacity>
                )}
              </View>
              
              <View style={{ marginBottom: 10 }}>
                <Text style={{ fontSize: 14, color: '#2c3e50', marginBottom: 5 }}>Item Name</Text>
                <TextInput
                  style={globalStyles.complaintTextInput}
                  placeholder="Enter item name"
                  value={item.itemName}
                  onChangeText={(value) => handleItemChange(item.id, 'itemName', value)}
                />
              </View>
              
              <View style={{ flexDirection: 'row', marginBottom: 10 }}>
                <View style={{ flex: 1, marginRight: 10 }}>
                  <Text style={{ fontSize: 14, color: '#2c3e50', marginBottom: 5 }}>Quantity</Text>
                  <TextInput
                    style={globalStyles.complaintTextInput}
                    placeholder="Qty"
                    value={item.quantity}
                    onChangeText={(value) => handleItemChange(item.id, 'quantity', value)}
                    keyboardType="numeric"
                  />
                </View>
                <View style={{ flex: 1 }}>
                  <Text style={{ fontSize: 14, color: '#2c3e50', marginBottom: 5 }}>Unit</Text>
                  <TextInput
                    style={globalStyles.complaintTextInput}
                    placeholder="Unit"
                    value={item.unit}
                    onChangeText={(value) => handleItemChange(item.id, 'unit', value)}
                  />
                </View>
              </View>
              
              <View>
                <Text style={{ fontSize: 14, color: '#2c3e50', marginBottom: 5 }}>Description</Text>
                <TextInput
                  style={globalStyles.complaintTextInput}
                  placeholder="Enter description"
                  value={item.description}
                  onChangeText={(value) => handleItemChange(item.id, 'description', value)}
                />
              </View>
            </View>
          ))}
        </View>

        {/* Reason */}
        <View style={globalStyles.complaintFieldContainer}>
          <Text style={globalStyles.complaintFieldLabel}>Reason for Requisition</Text>
          <TextInput
            style={globalStyles.complaintMessageInput}
            placeholder="Enter reason for requisition..."
            value={formData.reason}
            onChangeText={(value) => handleInputChange('reason', value)}
            multiline
            numberOfLines={3}
            textAlignVertical="top"
          />
        </View>

        {/* Approval Status */}
        <View style={globalStyles.complaintFieldContainer}>
          <Text style={globalStyles.complaintFieldLabel}>Approval Status:</Text>
          <View style={globalStyles.complaintDropdownContainer}>
            <TextInput
              style={globalStyles.complaintDropdownInput}
              placeholder="Select Status"
              value={formData.approvalStatus}
              onChangeText={(value) => handleInputChange('approvalStatus', value)}
            />
            <Ionicons name="chevron-down" size={20} color="#666" />
          </View>
        </View>
      </ScrollView>
    </SafeAreaView>
  );
};

export default MaterialRequisitionScreen;
