import React, { useState } from 'react';
import {
  View,
  Text,
  TouchableOpacity,
  SafeAreaView,
  StatusBar,
  FlatList,
} from 'react-native';
import { Ionicons } from '@expo/vector-icons';
import { globalStyles } from '../styles/globalStyles';

interface LeaveItem {
  id: number;
  type: string;
  fromDate: string;
  toDate: string;
  status: string;
  reason: string;
}

interface LeaveListScreenProps {
  onBack: () => void;
  onAddNew: () => void;
}

const LeaveListScreen: React.FC<LeaveListScreenProps> = ({ onBack, onAddNew }) => {
  const [leaveItems] = useState<LeaveItem[]>([]); // Empty list for now

  const renderEmptyState = () => (
    <View style={{
      flex: 1,
      justifyContent: 'center',
      alignItems: 'center',
      paddingHorizontal: 40,
    }}>
      <Ionicons name="calendar-outline" size={64} color="#bdc3c7" />
      <Text style={{
        fontSize: 18,
        color: '#7f8c8d',
        marginTop: 16,
        textAlign: 'center',
        fontWeight: '500',
      }}>
        No leave requests yet
      </Text>
      <Text style={{
        fontSize: 14,
        color: '#95a5a6',
        marginTop: 8,
        textAlign: 'center',
        lineHeight: 20,
      }}>
        Tap "Add new" to create your first leave request
      </Text>
    </View>
  );

  const renderLeaveItem = ({ item }: { item: LeaveItem }) => (
    <View style={{
      backgroundColor: '#fff',
      marginHorizontal: 20,
      marginVertical: 8,
      borderRadius: 12,
      padding: 16,
      shadowColor: '#000',
      shadowOffset: { width: 0, height: 2 },
      shadowOpacity: 0.1,
      shadowRadius: 4,
      elevation: 3,
    }}>
      <View style={{ flexDirection: 'row', justifyContent: 'space-between', alignItems: 'center', marginBottom: 8 }}>
        <Text style={{ fontSize: 16, fontWeight: '600', color: '#2c3e50' }}>{item.type}</Text>
        <View style={{
          backgroundColor: item.status === 'Approved' ? '#2ecc71' : item.status === 'Pending' ? '#f39c12' : '#e74c3c',
          paddingHorizontal: 8,
          paddingVertical: 4,
          borderRadius: 12,
        }}>
          <Text style={{ color: '#fff', fontSize: 12, fontWeight: '600' }}>{item.status}</Text>
        </View>
      </View>
      <Text style={{ fontSize: 14, color: '#7f8c8d', marginBottom: 4 }}>
        {item.fromDate} - {item.toDate}
      </Text>
      <Text style={{ fontSize: 14, color: '#2c3e50' }} numberOfLines={2}>
        {item.reason}
      </Text>
    </View>
  );

  return (
    <SafeAreaView style={globalStyles.complaintContainer}>
      <StatusBar barStyle="light-content" backgroundColor="#3498db" />
      
      {/* Header */}
      <View style={globalStyles.complaintHeader}>
        <TouchableOpacity onPress={onBack} style={globalStyles.complaintBackButton}>
          <Ionicons name="arrow-back" size={24} color="#fff" />
        </TouchableOpacity>
        
        <Text style={{ color: '#fff', fontSize: 18, fontWeight: 'bold' }}>Leave</Text>
        
        <TouchableOpacity onPress={onAddNew} style={{ flexDirection: 'row', alignItems: 'center' }}>
          <Ionicons name="add" size={20} color="#fff" />
          <Text style={{ color: '#fff', fontSize: 16, fontWeight: '600', marginLeft: 4 }}>Add new</Text>
        </TouchableOpacity>
      </View>

      {/* Content */}
      <View style={{ flex: 1, backgroundColor: '#f8f9fa' }}>
        {leaveItems.length === 0 ? (
          renderEmptyState()
        ) : (
          <FlatList
            data={leaveItems}
            renderItem={renderLeaveItem}
            keyExtractor={(item) => item.id.toString()}
            contentContainerStyle={{ paddingVertical: 20 }}
            showsVerticalScrollIndicator={false}
          />
        )}
      </View>
    </SafeAreaView>
  );
};

export default LeaveListScreen;
