import React, { useState } from 'react';
import {
  View,
  Text,
  TouchableOpacity,
  SafeAreaView,
  ScrollView,
  StatusBar,
  FlatList,
} from 'react-native';
import { Ionicons } from '@expo/vector-icons';
import { globalStyles } from '../styles/globalStyles';

interface LeaveRequest {
  id: string;
  type: string;
  startDate: string;
  endDate: string;
  status: 'Pending' | 'Approved' | 'Rejected';
  days: number;
}

interface LeaveListScreenProps {
  onBack: () => void;
  onAddNew: () => void;
}

const LeaveListScreen: React.FC<LeaveListScreenProps> = ({ onBack, onAddNew }) => {
  // Sample leave requests data
  const [leaveRequests] = useState<LeaveRequest[]>([
    {
      id: '1',
      type: 'Sick Leave',
      startDate: '2024-01-15',
      endDate: '2024-01-17',
      status: 'Approved',
      days: 3,
    },
    {
      id: '2',
      type: 'Annual Leave',
      startDate: '2024-02-01',
      endDate: '2024-02-05',
      status: 'Pending',
      days: 5,
    },
  ]);

  const getStatusColor = (status: string): string => {
    switch (status) {
      case 'Approved':
        return '#2ecc71';
      case 'Pending':
        return '#f39c12';
      case 'Rejected':
        return '#e74c3c';
      default:
        return '#95a5a6';
    }
  };

  const renderLeaveItem = ({ item }: { item: LeaveRequest }) => (
    <View style={globalStyles.leaveItemContainer}>
      <View style={globalStyles.leaveItemHeader}>
        <Text style={globalStyles.leaveItemType}>{item.type}</Text>
        <View style={[globalStyles.leaveStatusBadge, { backgroundColor: getStatusColor(item.status) }]}>
          <Text style={globalStyles.leaveStatusText}>{item.status}</Text>
        </View>
      </View>
      <View style={globalStyles.leaveItemDetails}>
        <View style={globalStyles.leaveDateContainer}>
          <Ionicons name="calendar-outline" size={16} color="#7f8c8d" />
          <Text style={globalStyles.leaveDateText}>
            {item.startDate} - {item.endDate}
          </Text>
        </View>
        <View style={globalStyles.leaveDaysContainer}>
          <Ionicons name="time-outline" size={16} color="#7f8c8d" />
          <Text style={globalStyles.leaveDaysText}>{item.days} days</Text>
        </View>
      </View>
    </View>
  );

  return (
    <SafeAreaView style={globalStyles.leaveContainer}>
      <StatusBar barStyle="light-content" backgroundColor="#3498db" />
      
      {/* Header */}
      <View style={globalStyles.leaveHeader}>
        <TouchableOpacity onPress={onBack} style={globalStyles.leaveBackButton}>
          <Ionicons name="arrow-back" size={24} color="#fff" />
        </TouchableOpacity>
        
        <Text style={globalStyles.leaveHeaderTitle}>Leave</Text>
        
        <TouchableOpacity 
          onPress={() => {
            console.log('Add new button pressed - calling onAddNew');
            onAddNew();
            console.log('onAddNew called');
          }} 
          style={globalStyles.leaveAddButton}
          activeOpacity={0.7}
        >
          <Ionicons name="add" size={20} color="#fff" />
          <Text style={globalStyles.leaveAddText}>Add new</Text>
        </TouchableOpacity>
      </View>

      {/* Content */}
      <View style={globalStyles.leaveContent}>
        {leaveRequests.length > 0 ? (
          <FlatList
            data={leaveRequests}
            renderItem={renderLeaveItem}
            keyExtractor={(item) => item.id}
            showsVerticalScrollIndicator={false}
            contentContainerStyle={globalStyles.leaveListContainer}
          />
        ) : (
          <View style={globalStyles.leaveEmptyContainer}>
            <Ionicons name="calendar-outline" size={64} color="#bdc3c7" />
            <Text style={globalStyles.leaveEmptyText}>No leave requests found</Text>
            <Text style={globalStyles.leaveEmptySubtext}>Tap "Add new" to create your first leave request</Text>
          </View>
        )}
      </View>
    </SafeAreaView>
  );
};

export default LeaveListScreen;
