import React, { useState } from 'react';
import {
  View,
  Text,
  StyleSheet,
  SafeAreaView,
  TouchableOpacity,
  ScrollView,
  StatusBar,
} from 'react-native';
import { Ionicons } from '@expo/vector-icons';
import { HomeScreenProps, MenuItem } from '../../types';

const HomeScreen: React.FC<HomeScreenProps> = ({ onLogout }) => {

  const menuItems: MenuItem[] = [
    {
      id: 1,
      title: 'Tickets',
      icon: 'document-text-outline',
      color: '#3498db',
    },
    {
      id: 2,
      title: 'Routine Services',
      icon: 'settings-outline',
      color: '#3498db',
    },
    {
      id: 3,
      title: 'Customers',
      icon: 'people-outline',
      color: '#3498db',
    },
    {
      id: 4,
      title: 'AMC',
      icon: 'calendar-outline',
      color: '#9b59b6',
    },
  ];

  const handleMenuPress = (item: MenuItem): void => {
    console.log(`Pressed: ${item.title}`);
    // Add navigation logic here
  };

  const handleMarkAttendance = (): void => {
    console.log('Mark Attendance In pressed');
    // Add attendance logic here
  };

  const handleWorkCheckIn = (): void => {
    console.log('Work Check In pressed');
    // Add check-in logic here
  };

  return (
    <SafeAreaView style={styles.container}>
      <StatusBar barStyle="light-content" backgroundColor="#3498db" />
      
      {/* Header */}
      <View style={styles.header}>
        <View style={styles.headerBottom}>
          <Ionicons name="menu" size={24} color="#fff" />
        </View>
      </View>

      <ScrollView style={styles.content} showsVerticalScrollIndicator={false}>
        {/* Menu Cards Grid */}
        <View style={styles.menuGrid}>
          {menuItems.map((item) => (
            <TouchableOpacity
              key={item.id}
              style={styles.menuCard}
              onPress={() => handleMenuPress(item)}
            >
              <View style={[styles.iconContainer, { backgroundColor: item.color }]}>
                <Ionicons name={item.icon} size={32} color="#fff" />
              </View>
              <Text style={styles.menuTitle}>{item.title}</Text>
            </TouchableOpacity>
          ))}
        </View>

        {/* Action Buttons */}
        <View style={styles.actionButtons}>
          <TouchableOpacity style={styles.actionButton} onPress={handleMarkAttendance}>
            <Text style={styles.actionButtonText}>Mark Attendance In</Text>
          </TouchableOpacity>
          
          <TouchableOpacity style={styles.actionButton} onPress={handleWorkCheckIn}>
            <Text style={styles.actionButtonText}>Work Check In</Text>
          </TouchableOpacity>
        </View>

        {/* Status Text */}
        <View style={styles.statusContainer}>
          <Text style={styles.statusText}>Last Check In: null || null ||</Text>
          <Ionicons name="location-outline" size={16} color="#3498db" />
        </View>
      </ScrollView>
    </SafeAreaView>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#f8f9fa',
  },
  header: {
    backgroundColor: '#3498db',
    paddingTop: 10,
    paddingBottom: 20,
    paddingHorizontal: 20,
  },
  headerBottom: {
    flexDirection: 'row',
    alignItems: 'center',
  },
  content: {
    flex: 1,
    paddingHorizontal: 20,
    paddingTop: 20,
  },
  menuGrid: {
    flexDirection: 'row',
    flexWrap: 'wrap',
    justifyContent: 'space-between',
    marginBottom: 30,
  },
  menuCard: {
    width: '48%',
    backgroundColor: '#fff',
    borderRadius: 12,
    padding: 20,
    alignItems: 'center',
    marginBottom: 16,
    shadowColor: '#000',
    shadowOffset: {
      width: 0,
      height: 2,
    },
    shadowOpacity: 0.1,
    shadowRadius: 4,
    elevation: 3,
  },
  iconContainer: {
    width: 60,
    height: 60,
    borderRadius: 30,
    justifyContent: 'center',
    alignItems: 'center',
    marginBottom: 12,
  },
  menuTitle: {
    fontSize: 16,
    fontWeight: '600',
    color: '#2c3e50',
    textAlign: 'center',
  },
  actionButtons: {
    marginBottom: 20,
  },
  actionButton: {
    backgroundColor: '#3498db',
    borderRadius: 12,
    paddingVertical: 16,
    alignItems: 'center',
    marginBottom: 12,
    shadowColor: '#3498db',
    shadowOffset: {
      width: 0,
      height: 4,
    },
    shadowOpacity: 0.3,
    shadowRadius: 8,
    elevation: 4,
  },
  actionButtonText: {
    color: '#fff',
    fontSize: 16,
    fontWeight: '600',
  },
  statusContainer: {
    flexDirection: 'row',
    alignItems: 'center',
    justifyContent: 'center',
    marginBottom: 20,
  },
  statusText: {
    fontSize: 14,
    color: '#2c3e50',
    marginRight: 8,
  },
});

export default HomeScreen;
