import React, { useState } from 'react';
import {
  View,
  Text,
  SafeAreaView,
  TouchableOpacity,
  ScrollView,
  StatusBar,
  Modal,
} from 'react-native';
import { Ionicons } from '@expo/vector-icons';
import { globalStyles } from '../styles/globalStyles';

interface RoutineMaintenanceScreenProps {
  onBack: () => void;
}

interface MaintenanceItem {
  id: number;
  title: string;
  icon: keyof typeof import('@expo/vector-icons').Ionicons.glyphMap;
  color: string;
}

const RoutineMaintenanceScreen: React.FC<RoutineMaintenanceScreenProps> = ({ onBack }) => {
  const [showNoDataModal, setShowNoDataModal] = useState(false);
  const [selectedItemTitle, setSelectedItemTitle] = useState('');

  const maintenanceItems: MaintenanceItem[] = [
    {
      id: 1,
      title: "Today's Services",
      icon: 'settings-outline' as const,
      color: '#3498db',
    },
    {
      id: 2,
      title: 'This Month Due',
      icon: 'settings-outline' as const,
      color: '#3498db',
    },
    {
      id: 3,
      title: 'This Month Overdue',
      icon: 'settings-outline' as const,
      color: '#3498db',
    },
    {
      id: 4,
      title: 'Last Month Overdue',
      icon: 'settings-outline' as const,
      color: '#3498db',
    },
    {
      id: 5,
      title: 'More Filter',
      icon: 'settings-outline' as const,
      color: '#3498db',
    },
  ];

  const handleItemPress = (item: MaintenanceItem): void => {
    // Show "No data found" for first four items
    if (item.id <= 4) {
      setSelectedItemTitle(item.title);
      setShowNoDataModal(true);
    } else {
      console.log(`Pressed: ${item.title}`);
      // Add navigation logic here for other maintenance categories
    }
  };

  return (
    <SafeAreaView style={globalStyles.routineMaintenanceContainer}>
      <StatusBar barStyle="light-content" backgroundColor="#3498db" />
      
      {/* Header */}
      <View style={globalStyles.routineMaintenanceHeader}>
        <TouchableOpacity onPress={onBack} style={globalStyles.routineMaintenanceBackButton}>
          <Ionicons name="arrow-back" size={24} color="#fff" />
        </TouchableOpacity>
        <Text style={globalStyles.routineMaintenanceTitle}>Routine Maintenance</Text>
        <View style={globalStyles.routineMaintenanceHeaderSpacer} />
      </View>

      {/* Content */}
      <ScrollView style={globalStyles.routineMaintenanceContent} showsVerticalScrollIndicator={false}>
        {maintenanceItems.map((item) => (
          <TouchableOpacity
            key={item.id}
            style={globalStyles.routineMaintenanceItem}
            onPress={() => handleItemPress(item)}
          >
            <View style={globalStyles.routineMaintenanceItemLeft}>
              <View style={[globalStyles.routineMaintenanceIconContainer, { backgroundColor: item.color }]}>
                <Ionicons name={item.icon} size={24} color="#fff" />
              </View>
              <Text style={globalStyles.routineMaintenanceItemText}>{item.title}</Text>
            </View>
            <Ionicons name="chevron-forward" size={20} color="#2c3e50" />
          </TouchableOpacity>
        ))}
      </ScrollView>

      {/* No Data Found Modal */}
      <Modal
        visible={showNoDataModal}
        transparent={true}
        animationType="fade"
        onRequestClose={() => setShowNoDataModal(false)}
      >
        <View style={globalStyles.tipsModalOverlay}>
          <View style={globalStyles.tipsModalContainer}>
            <View style={globalStyles.tipsModalHeader}>
              <Text style={globalStyles.tipsModalTitle}>No Data Found</Text>
              <TouchableOpacity
                onPress={() => setShowNoDataModal(false)}
                style={globalStyles.tipsModalCloseButton}
              >
                <Ionicons name="close" size={24} color="#2c3e50" />
              </TouchableOpacity>
            </View>
            <View style={globalStyles.tipsModalContent}>
              <View style={globalStyles.tipsModalTipItem}>
                <Text style={globalStyles.tipsModalTipContent}>
                  No data available for "{selectedItemTitle}" at the moment.
                </Text>
              </View>
            </View>
            <View style={globalStyles.tipsModalFooter}>
              <TouchableOpacity
                onPress={() => setShowNoDataModal(false)}
                style={globalStyles.tipsModalCloseButton}
              >
                <Text style={globalStyles.tipsModalCloseButtonText}>Close</Text>
              </TouchableOpacity>
            </View>
          </View>
        </View>
      </Modal>
    </SafeAreaView>
  );
};

export default RoutineMaintenanceScreen;
